package string;

public class StringEndsWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s= "Python";
		System.out.println(s.endsWith("on"));
		System.out.println(s.endsWith("On"));
		System.out.println(s.endsWith("Py"));
		// to check whether a string is ending with....
	}

}
